import { useState } from "react";
import io from "socket.io-client";

const socket = io();

const BidBot = () => {
	const [connected, setconnection] = useState(socket.connected);
	const [date, setDate] = useState(null);

	const addBid = () => {};

	useEffect(() => {
		socket.on("connect", () => {
			setconnection(true);
		});

		socket.on("disconnect", () => {
			setconnection(false);
		});

		socket.on("pong", () => {
			setconnection(new Date().toISOString());
		});

		return () => {
			socket.off("connect");
			socket.off("disconnect");
			socket.off("pong");
		};
	}, []);

	return (
		<>
			<button onClick={addBid}>Add Bid</button>
		</>
	);
};

export default BidBot;
